import React, { useState, useEffect } from 'react'
import ReactDOM from 'react-dom'

const Button = ({handleClick, text}) => (
    <button onClick={handleClick}>
      {text}
    </button>
)

const Statistics = (props) => {
  let [total, setTotal] = useState(props.good+props.neutral+props.bad)
  let [average, setAverage] = useState(0)
  let [positive, setPositive] = useState(0)

  useEffect(
    (total) => {
      if(total !== 0) {
        setAverage((props.good-props.bad)/total)
        setPositive((props.good/total)*100)
      }
    },
    [props.good, props.neutral, props.bad, total],
  )

  return (
    <div>
      <h2>statistics</h2>
      <div>good {props.good}</div>
      <div>neutral {props.neutral}</div>
      <div>bad {props.bad}</div>
      <div>all {total}</div>
      <div>average {average}</div>
      <div>positive {positive}%</div>
    </div>
  )
}

const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <h1>give feedback</h1>
      <Button handleClick={() => setGood(good + 1)} text="good" />
      <Button handleClick={() => setNeutral(neutral + 1)} text="neutral" />
      <Button handleClick={() => setBad(bad + 1)} text="bad" />
      <Statistics good={good} neutral={neutral} bad={bad} />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
